import { useLocation } from "react-router-dom";
import { FaBars, FaTimes } from "react-icons/fa";
import "./NavbarStyles.css";

const Navbar = () => {
  const [click, setClick] = useState(false);
  const handleClick = () => setClick(!click);
  const handleClose = () => setClick(false);
  const location = useLocation();

  const [colour, setColour] = useState(false);
  const changeColour = () => {
    if (window.scrollY >= 1) {
      setColour(true);
    } else {
      setColour(false);
    }
  };

  window.addEventListener("scroll", changeColour);

  return (
    <div className={colour ? "header header-bg" : "header"}>
      <Link to="/">
        <h1 className="nav_text"> Nicholas Gin </h1>
      </Link>

      <ul className={click ? "nav_tabs active" : "nav_tabs"}>
        <li
          className={location.pathname === "/" ? "active" : ""}
          onClick={handleClose}
        >
          <Link to="/" className="nav_text">
            Home
          </Link>
        </li>
        <li
          className={location.pathname === "/about" ? "active" : ""}
          onClick={handleClose}
        >
          <Link to="/about" className="nav_text">
            About Me
          </Link>
        </li>
        <li
          className={location.pathname === "/projects" ? "active" : ""}
          onClick={handleClose}
        >
          <Link to="/projects" className="nav_text">
            Projects
          </Link>
        </li>
      </ul>

      <div className="burger" onClick={handleClick}>
        {click ? <FaTimes size={30} style={{ color: "white" }} /> : <FaBars size={30} style={{ color: "white" }} />}
      </div>
    </div>
  );
};

export default Navbar;
