import React from "react";
import HomeScreen from "../components/HomeScreen";
import { motion, useScroll } from "framer-motion/dist/framer-motion";

function Component() {
  const { scrollYProgress } = useScroll();

  return (
    <motion.div style={{ scaleX: scrollYProgress }} />  
  )
}

const Home = () => {
  return (
    <motion.div
      initial = {{opacity: 0}}
      animate = {{opacity: 1}}
      exit = {{opacity: 0}}>

      <Component/>
      <HomeScreen/>
    </motion.div>);
}

export default Home
