import "./HomeScreenStyles.css";

import React from "react";
import { Link } from "react-router-dom";
import { useEffect, useRef } from "react";
import { init } from "ityped";
import { motion } from "framer-motion/dist/framer-motion";

const HomeScreen = () => {

  const hello = useRef();
  const name = useRef();

  useEffect(() => {
    init(hello.current, 
      {
        showCursor: true,
        strings: ["HELLO"],
        loop: false,
        showCursor: false,
        typeSpeed: 200,
        startDelay: 0,
      })
    console.log(hello)
  },[])


  useEffect(() => {
    init(name.current, 
      {
        showCursor: true,
        strings: ["I'm Nicholas!"],
        loop: false,
        showCursor: false,
        typeSpeed: 100,
        startDelay: 0,
      })
    console.log(name)
  },[])


  return (
    <div className ="main_screen">
        <div className = "info">
          <h1> <span ref = {hello}></span> </h1> 
          <p> <span ref = {name}></span> </p>
          <motion.div 
            className = "buttons_layout"
            initial = {{opacity: 0}}
            animate = {{opacity: 0.25}}
            exit = {{opacity: 0}}>


            <Link to = "/about" className = "btn"> About Me </Link>
            <Link to = "/projects" className = "btn"> Projects </Link>
            </motion.div>
        </div>
    </div>
  )
}

export default HomeScreen
